import 'package:flutter/material.dart';import 'package:sizer/sizer.dart';import '../../shared/constants/colors.dart';import '../resources.dart';class Loader extends StatelessWidget {  const Loader({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return Center(child: Container      (      width: 5.h,      height: 5.h,      padding: EdgeInsets.all(5),      decoration: BoxDecoration        (        shape: BoxShape.circle,        border: Border.all(color: AppColors.kMainColor.withOpacity(.5),width: 2),        image: DecorationImage(image: AssetImage(Resources.loader),fit: BoxFit.cover)        ),      ));  }}